mapper.select:
select a.id,u.name as owner,a.name,a.start_date,a.end_date,a.cost,a.description,a.create_time,s.name as create_by, a.edit_time,e.name as edit_by from tbl_activity a join tbl_user u on u.id=a.owner join tbl_user s on s.id=a.create_by left join tbl_user e on e.id=a.edit_by
and u.name like '%' #{owner} '%'
and a.name like '%' #{name} '%'
and a.start_date>=#{startDate}
and a.end_date<=#{endDate}
order by a.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_activity a join tbl_user u on u.id=a.owner join tbl_user s on s.id=a.create_by left join tbl_user e on e.id=a.edit_by
and u.name like '%' #{owner} '%'
and a.name like '%' #{name} '%'
and a.start_date>=#{startDate}
and a.end_date<=#{endDate}
